import React, { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../AuthContext';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';

function RatingForm() {
  const { user } = useContext(AuthContext);
  const [courses, setCourses] = useState([]);
  const [lecturers, setLecturers] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState('');
  const [selectedLecturer, setSelectedLecturer] = useState('');
  const [rating, setRating] = useState(0);
  const [hoverRating, setHoverRating] = useState(0);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Fetch enrolled courses on mount
  useEffect(() => {
    const fetchEnrolledCourses = async () => {
      try {
        const response = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:4000'}/student/enrolled-courses`, {
          headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
        });
        const data = await response.json();
        if (response.ok) {
          setCourses(data);
        } else {
          setError(data.error || 'Failed to fetch enrolled courses');
        }
      } catch (err) {
        setError('Error fetching enrolled courses: ' + err.message);
      }
    };
    fetchEnrolledCourses();
  }, []);

  // Fetch lecturers for selected course
  useEffect(() => {
    const fetchLecturers = async () => {
      if (selectedCourse) {
        try {
          const response = await fetch(
            `${process.env.REACT_APP_API_URL || 'http://localhost:4000'}/student/course-lecturers?course_id=${selectedCourse}`,
            {
              headers: { 'Authorization': `Bearer ${localStorage.getItem('token')}` }
            }
          );
          const data = await response.json();
          if (response.ok) {
            setLecturers(data);
            setSelectedLecturer('');
          } else {
            setError(data.error || 'Failed to fetch lecturers');
          }
        } catch (err) {
          setError('Error fetching lecturers: ' + err.message);
        }
      } else {
        setLecturers([]);
        setSelectedLecturer('');
      }
    };
    fetchLecturers();
  }, [selectedCourse]);

  // Handle rating submission
  const handleSubmitRating = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    if (!selectedCourse || !selectedLecturer || rating < 1 || rating > 5) {
      setError('Please select a course, lecturer, and provide a rating (1–5 stars)');
      return;
    }

    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:4000'}/student/rate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ course_id: selectedCourse, lecturer_id: selectedLecturer, rating })
      });
      const data = await response.json();
      if (response.ok) {
        setSuccess('Rating submitted successfully');
        setSelectedCourse('');
        setSelectedLecturer('');
        setRating(0);
        setHoverRating(0);
      } else {
        setError(data.error || 'Failed to submit rating');
      }
    } catch (err) {
      setError('Error submitting rating: ' + err.message);
    }
  };

  // Handle star rating click
  const handleStarClick = (value) => {
    setRating(value);
  };

  // Handle star hover
  const handleStarHover = (value) => {
    setHoverRating(value);
  };

  // Reset hover state
  const handleStarLeave = () => {
    setHoverRating(0);
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4"><i className="bi bi-star-fill me-2"></i>Rate a Lecturer</h2>
      {error && <div className="alert alert-danger">{error}</div>}
      {success && <div className="alert alert-success">{success}</div>}
      <div className="card shadow-sm">
        <div className="card-body">
          <form onSubmit={handleSubmitRating}>
            <div className="mb-3">
              <label htmlFor="courseSelect" className="form-label">Course</label>
              <select
                id="courseSelect"
                className="form-select"
                value={selectedCourse}
                onChange={(e) => setSelectedCourse(e.target.value)}
                required
                aria-required="true"
              >
                <option value="">Select Course</option>
                {courses.map(course => (
                  <option key={course.id} value={course.id}>{course.name} ({course.code})</option>
                ))}
              </select>
            </div>
            <div className="mb-3">
              <label htmlFor="lecturerSelect" className="form-label">Lecturer</label>
              <select
                id="lecturerSelect"
                className="form-select"
                value={selectedLecturer}
                onChange={(e) => setSelectedLecturer(e.target.value)}
                required
                aria-required="true"
                disabled={!selectedCourse}
              >
                <option value="">Select Lecturer</option>
                {lecturers.map(lecturer => (
                  <option key={lecturer.id} value={lecturer.id}>{lecturer.full_name}</option>
                ))}
              </select>
            </div>
            <div className="mb-3">
              <label className="form-label">Rating</label>
              <div className="d-flex align-items-center">
                {[1, 2, 3, 4, 5].map(star => (
                  <i
                    key={star}
                    className={`bi ${star <= (hoverRating || rating) ? 'bi-star-fill' : 'bi-star'} fs-3 me-2`}
                    style={{ cursor: 'pointer', color: star <= (hoverRating || rating) ? '#ffc107' : '#6c757d' }}
                    onClick={() => handleStarClick(star)}
                    onMouseEnter={() => handleStarHover(star)}
                    onMouseLeave={handleStarLeave}
                    role="button"
                    aria-label={`Rate ${star} star${star > 1 ? 's' : ''}`}
                  />
                ))}
              </div>
            </div>
            <button type="submit" className="btn btn-primary" disabled={!selectedCourse || !selectedLecturer || rating < 1}>
              <i className="bi bi-check-circle me-2"></i>Submit Rating
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default RatingForm;
