import React, { useState, useEffect, useContext } from 'react';
   import { useNavigate, Link } from 'react-router-dom';
   import { AuthContext } from '../AuthContext';
   import 'bootstrap/dist/css/bootstrap.min.css';
   import 'bootstrap-icons/font/bootstrap-icons.css';

   function PRLReportForm() {
     const { user, logout } = useContext(AuthContext);
     const navigate = useNavigate();
     const [formData, setFormData] = useState({
       lecturer_id: '',
       class_id: '',
       week_of_reporting: '',
       date_of_lecture: '',
       course_id: '',
       actual_students_present: '',
       total_registered_students: '',
       venue_id: '',
       scheduled_lecture_time: '',
       topic_taught: '',
       learning_outcomes: '',
       recommendations: '',
       prl_feedback: '',
     });
     const [lecturers, setLecturers] = useState([]);
     const [courses, setCourses] = useState([]);
     const [filteredCourses, setFilteredCourses] = useState([]);
     const [venues, setVenues] = useState([]);
     const [classes, setClasses] = useState([]);
     const [filteredClasses, setFilteredClasses] = useState([]);
     const [faculties, setFaculties] = useState([]);
     const [selectedFaculty, setSelectedFaculty] = useState('');
     const [newVenue, setNewVenue] = useState('');
     const [newClass, setNewClass] = useState({ name: '' });
     const [error, setError] = useState('');
     const [message, setMessage] = useState('');
     const token = localStorage.getItem('token');
     const apiUrl = process.env.REACT_APP_API_URL || 'http://localhost:4000';

     useEffect(() => {
       if (!user || user.role !== 'PRL') {
         navigate('/unauthorized');
         return;
       }
       if (!token) {
         logout();
         navigate('/login');
         return;
       }

       const fetchLecturersBase = async () => {
         try {
           const res = await fetch(`${apiUrl}/users?role=Lecturer`, {
             headers: { Authorization: `Bearer ${token}` },
           });
           if (res.status === 401) {
             logout();
             navigate('/login');
             return;
           }
           if (!res.ok) {
             const data = await res.json().catch(() => ({}));
             throw new Error(data.error || 'Failed to fetch lecturers');
           }
           const data = await res.json();
           setLecturers(Array.isArray(data) ? data : []);
         } catch (err) {
           console.error('Fetch lecturers error:', err);
         }
       };

       const fetchCourses = async () => {
         try {
           const res = await fetch(`${apiUrl}/courses`, {
             headers: { Authorization: `Bearer ${token}` },
           });
           if (res.status === 401) {
             logout();
             navigate('/login');
             return;
           }
           if (!res.ok) {
             const data = await res.json().catch(() => ({}));
             throw new Error(data.error || 'Failed to fetch courses');
           }
           const data = await res.json();
           setCourses(Array.isArray(data) ? data : []);
         } catch (err) {
           console.error('Fetch courses error:', err);
         }
       };

       const fetchVenues = async () => {
         try {
           const res = await fetch(`${apiUrl}/venues`, {
             headers: { Authorization: `Bearer ${token}` },
           });
           if (res.status === 401) {
             logout();
             navigate('/login');
             return;
           }
           if (!res.ok) {
             const data = await res.json().catch(() => ({}));
             throw new Error(data.error || 'Failed to fetch venues');
           }
           const data = await res.json();
           setVenues(Array.isArray(data) ? data : []);
         } catch (err) {
           console.error('Fetch venues error:', err);
         }
       };

       const fetchClasses = async () => {
         try {
           const res = await fetch(`${apiUrl}/classes`, {
             headers: { Authorization: `Bearer ${token}` },
           });
           if (res.status === 401) {
             logout();
             navigate('/login');
             return;
           }
           if (!res.ok) {
             const data = await res.json().catch(() => ({}));
             throw new Error(data.error || 'Failed to fetch classes');
           }
           const data = await res.json();
           setClasses(Array.isArray(data) ? data : []);
         } catch (err) {
           console.error('Fetch classes error:', err);
           setError('Failed to load classes. Other data may still be available.');
         }
       };

       const fetchFaculties = async () => {
         try {
           const res = await fetch(`${apiUrl}/faculties`, {
             headers: { Authorization: `Bearer ${token}` },
           });
           if (res.status === 401) {
             logout();
             navigate('/login');
             return;
           }
           if (!res.ok) {
             const data = await res.json().catch(() => ({}));
             throw new Error(data.error || 'Failed to fetch faculties');
           }
           const data = await res.json();
           setFaculties(Array.isArray(data) ? data : []);
         } catch (err) {
           console.error('Fetch faculties error:', err);
         }
       };

       fetchLecturersBase();
       fetchCourses();
       fetchVenues();
       fetchClasses();
       fetchFaculties();
     }, [user, navigate, logout, token]);

     useEffect(() => {
       const fetchLecturers = async () => {
         let url = `${apiUrl}/users?role=Lecturer`;
         if (selectedFaculty) {
           url += `&faculty_id=${selectedFaculty}`;
         }
         try {
           const res = await fetch(url, {
             headers: { Authorization: `Bearer ${token}` },
           });
           if (res.status === 401) {
             logout();
             navigate('/login');
             return;
           }
           if (!res.ok) {
             const data = await res.json().catch(() => ({}));
             throw new Error(data.error || 'Failed to fetch lecturers');
           }
           const data = await res.json();
           setLecturers(Array.isArray(data) ? data : []);
         } catch (err) {
           console.error('Fetch lecturers error:', err);
         }
       };

       fetchLecturers();
     }, [selectedFaculty, token, logout, navigate]);

     useEffect(() => {
       setFilteredCourses(selectedFaculty ? courses.filter(c => c.faculty_id === parseInt(selectedFaculty)) : courses);
     }, [selectedFaculty, courses]);

     useEffect(() => {
       setFilteredClasses(
         formData.course_id
           ? classes.filter(cls => cls.course_ids.includes(parseInt(formData.course_id)))
           : selectedFaculty
           ? classes.filter(cls => cls.course_ids.some(id => filteredCourses.some(c => c.id === id)))
           : classes
       );
     }, [selectedFaculty, classes, filteredCourses, formData.course_id]);

     useEffect(() => {
       setFormData(prev => ({ ...prev, lecturer_id: '', course_id: '', class_id: '' }));
     }, [selectedFaculty]);

     useEffect(() => {
       if (formData.course_id) {
         const selectedCourse = courses.find(course => course.id === parseInt(formData.course_id));
         if (selectedCourse) {
           setFormData(prev => ({
             ...prev,
             total_registered_students: selectedCourse.total_registered_students || 0,
           }));
         }
       }
     }, [formData.course_id, courses]);

     const handleChange = (e) => {
       const { name, value } = e.target;
       setFormData({ ...formData, [name]: value });
     };

     const handleVenueSubmit = async (e) => {
       e.preventDefault();
       setError('');
       setMessage('');
       if (!newVenue) {
         setError('Venue name is required');
         return;
       }
       try {
         const response = await fetch(`${apiUrl}/venues`, {
           method: 'POST',
           headers: {
             Authorization: `Bearer ${token}`,
             'Content-Type': 'application/json',
           },
           body: JSON.stringify({ name: newVenue }),
         });
         if (response.status === 401 || response.status === 403) {
           logout();
           navigate('/login');
           return;
         }
         if (!response.ok) {
           const errorData = await response.json();
           throw new Error(errorData.error || 'Failed to create venue');
         }
         const newVenueData = await response.json();
         setVenues([...venues, newVenueData]);
         setNewVenue('');
         setMessage('Venue created successfully!');
       } catch (err) {
         console.error('Create venue error:', err);
         setError(err.message || 'Failed to create venue');
       }
     };

     const handleClassSubmit = async (e) => {
       e.preventDefault();
       setError('');
       setMessage('');
       if (!newClass.name) {
         setError('Class Name is required');
         return;
       }
       try {
         const response = await fetch(`${apiUrl}/classes`, {
           method: 'POST',
           headers: {
             Authorization: `Bearer ${token}`,
             'Content-Type': 'application/json',
           },
           body: JSON.stringify({ name: newClass.name }),
         });
         if (response.status === 401 || response.status === 403) {
           logout();
           navigate('/login');
           return;
         }
         if (!response.ok) {
           const errorData = await response.json();
           throw new Error(errorData.error || 'Failed to create class');
         }
         const newClassData = await response.json();
         setClasses([...classes, newClassData]);
         setNewClass({ name: '' });
         setMessage('Class created successfully!');
       } catch (err) {
         console.error('Create class error:', err);
         setError(err.message || 'Failed to create class');
       }
     };

     const handleClassChange = (e) => {
       const { name, value } = e.target;
       setNewClass({ ...newClass, [name]: value });
     };

     const validateForm = () => {
       if (!formData.lecturer_id) return 'Lecturer is required';
       if (!formData.class_id) return 'Class is required';
       if (!formData.week_of_reporting || isNaN(parseInt(formData.week_of_reporting)) || parseInt(formData.week_of_reporting) < 1 || parseInt(formData.week_of_reporting) > 52) {
         return 'Week of reporting must be a number between 1 and 52';
       }
       if (!formData.date_of_lecture) return 'Date of lecture is required';
       if (!formData.course_id) return 'Course is required';
       if (formData.actual_students_present === '' || isNaN(parseInt(formData.actual_students_present)) || parseInt(formData.actual_students_present) < 0) {
         return 'Actual students present must be a non-negative number';
       }
       if (formData.total_registered_students === '' || isNaN(parseInt(formData.total_registered_students)) || parseInt(formData.total_registered_students) < 0) {
         return 'Total registered students must be a non-negative number';
       }
       if (!formData.venue_id) return 'Venue is required';
       if (!formData.scheduled_lecture_time) return 'Scheduled lecture time is required';
       if (!formData.topic_taught) return 'Topic taught is required';
       if (!formData.learning_outcomes) return 'Learning outcomes are required';
       return '';
     };

     const handleSubmit = async (e) => {
       e.preventDefault();
       setError('');
       setMessage('');

       const validationError = validateForm();
       if (validationError) {
         setError(validationError);
         return;
       }

       try {
         const response = await fetch(`${apiUrl}/prl/reports`, {
           method: 'POST',
           headers: {
             Authorization: `Bearer ${token}`,
             'Content-Type': 'application/json',
           },
           body: JSON.stringify({
             lecturer_id: parseInt(formData.lecturer_id),
             class_id: parseInt(formData.class_id),
             week_of_reporting: parseInt(formData.week_of_reporting),
             date_of_lecture: formData.date_of_lecture,
             course_id: parseInt(formData.course_id),
             actual_students_present: parseInt(formData.actual_students_present),
             total_registered_students: parseInt(formData.total_registered_students),
             venue_id: parseInt(formData.venue_id),
             scheduled_lecture_time: formData.scheduled_lecture_time,
             topic_taught: formData.topic_taught,
             learning_outcomes: formData.learning_outcomes,
             recommendations: formData.recommendations || null,
             prl_feedback: formData.prl_feedback || null,
           }),
         });

         if (response.status === 401 || response.status === 403) {
           logout();
           navigate('/login');
           return;
         }
         if (!response.ok) {
           const errorData = await response.json();
           throw new Error(errorData.error || 'Failed to create report');
         }
         const newReport = await response.json();
         setMessage('Report created successfully!');
         setFormData({
           lecturer_id: '',
           class_id: '',
           week_of_reporting: '',
           date_of_lecture: '',
           course_id: '',
           actual_students_present: '',
           total_registered_students: '',
           venue_id: '',
           scheduled_lecture_time: '',
           topic_taught: '',
           learning_outcomes: '',
           recommendations: '',
           prl_feedback: '',
         });
         setSelectedFaculty('');
         setTimeout(() => navigate('/prl/reports'), 2000);
       } catch (err) {
         console.error('Create report error:', err);
         setError(err.message || 'Failed to create report');
       }
     };

     return (
       <div className="container mt-5">
         <div className="d-flex justify-content-between align-items-center mb-4">
           <h2>Create PRL Report</h2>
           <div>
             <Link to="/prl/reports" className="btn btn-secondary me-2">View Reports</Link>
             <Link to="/prl/class-courses" className="btn btn-primary me-2">Manage Class-Course Assignments</Link>
             <button onClick={logout} className="btn btn-danger">Logout</button>
           </div>
         </div>

         {error && <div className="alert alert-danger">{error}</div>}
         {message && <div className="alert alert-success">{message}</div>}

         <div className="row">
           <div className="col-md-6">
             <div className="card mb-4">
               <div className="card-header">Create Report</div>
               <div className="card-body">
                 <form onSubmit={handleSubmit}>
                   <div className="mb-3">
                     <label htmlFor="faculty_id" className="form-label">Faculty</label>
                     <select
                       id="faculty_id"
                       className="form-select"
                       value={selectedFaculty}
                       onChange={(e) => setSelectedFaculty(e.target.value)}
                       aria-label="Select faculty"
                     >
                       <option value="">Select Faculty</option>
                       {faculties.map(faculty => (
                         <option key={faculty.id} value={faculty.id}>{faculty.name}</option>
                       ))}
                     </select>
                   </div>

                   <div className="mb-3">
                     <label htmlFor="lecturer_id" className="form-label">Lecturer <span className="text-danger">*</span></label>
                     <select
                       id="lecturer_id"
                       name="lecturer_id"
                       className="form-select"
                       value={formData.lecturer_id}
                       onChange={handleChange}
                       required
                       aria-label="Select lecturer"
                     >
                       <option value="">Select Lecturer</option>
                       {lecturers.map(lecturer => (
                         <option key={lecturer.id} value={lecturer.id}>{lecturer.full_name}</option>
                       ))}
                     </select>
                   </div>

                   <div className="mb-3">
                     <label htmlFor="course_id" className="form-label">Course <span className="text-danger">*</span></label>
                     <select
                       id="course_id"
                       name="course_id"
                       className="form-select"
                       value={formData.course_id}
                       onChange={handleChange}
                       required
                       aria-label="Select course"
                     >
                       <option value="">Select Course</option>
                       {filteredCourses.map(course => (
                         <option key={course.id} value={course.id}>{course.name}</option>
                       ))}
                     </select>
                   </div>

                   <div className="mb-3">
                     <label htmlFor="class_id" className="form-label">Class <span className="text-danger">*</span></label>
                     <select
                       id="class_id"
                       name="class_id"
                       className="form-select"
                       value={formData.class_id}
                       onChange={handleChange}
                       required
                       aria-label="Select class"
                     >
                       <option value="">Select Class</option>
                       {filteredClasses.map(cls => (
                         <option key={cls.id} value={cls.id}>
                           {cls.class_name} {cls.course_names.length > 0 ? `(${cls.course_names.join(', ')})` : ''}
                         </option>
                       ))}
                     </select>
                   </div>

                   <div className="mb-3">
                     <label htmlFor="week_of_reporting" className="form-label">Week of Reporting <span className="text-danger">*</span></label>
                     <input
                       type="number"
                       id="week_of_reporting"
                       name="week_of_reporting"
                       className="form-control"
                       value={formData.week_of_reporting}
                       onChange={handleChange}
                       required
                       min="1"
                       max="52"
                       aria-label="Week of reporting"
                     />
                   </div>

                   <div className="mb-3">
                     <label htmlFor="date_of_lecture" className="form-label">Date of Lecture <span className="text-danger">*</span></label>
                     <input
                       type="date"
                       id="date_of_lecture"
                       name="date_of_lecture"
                       className="form-control"
                       value={formData.date_of_lecture}
                       onChange={handleChange}
                       required
                       aria-label="Date of lecture"
                     />
                   </div>

                   <div className="mb-3">
                     <label htmlFor="actual_students_present" className="form-label">Actual Students Present <span className="text-danger">*</span></label>
                     <input
                       type="number"
                       id="actual_students_present"
                       name="actual_students_present"
                       className="form-control"
                       value={formData.actual_students_present}
                       onChange={handleChange}
                       required
                       min="0"
                       aria-label="Actual students present"
                     />
                   </div>

                   <div className="mb-3">
                     <label htmlFor="total_registered_students" className="form-label">Total Registered Students <span className="text-danger">*</span></label>
                     <input
                       type="number"
                       id="total_registered_students"
                       name="total_registered_students"
                       className="form-control"
                       value={formData.total_registered_students}
                       readOnly
                       aria-label="Total registered students"
                     />
                   </div>

                   <div className="mb-3">
                     <label htmlFor="venue_id" className="form-label">Venue <span className="text-danger">*</span></label>
                     <select
                       id="venue_id"
                       name="venue_id"
                       className="form-select"
                       value={formData.venue_id}
                       onChange={handleChange}
                       required
                       aria-label="Select venue"
                     >
                       <option value="">Select Venue</option>
                       {venues.map(venue => (
                         <option key={venue.id} value={venue.id}>{venue.name}</option>
                       ))}
                     </select>
                   </div>

                   <div className="mb-3">
                     <label htmlFor="scheduled_lecture_time" className="form-label">Scheduled Lecture Time <span className="text-danger">*</span></label>
                     <input
                       type="time"
                       id="scheduled_lecture_time"
                       name="scheduled_lecture_time"
                       className="form-control"
                       value={formData.scheduled_lecture_time}
                       onChange={handleChange}
                       required
                       aria-label="Scheduled lecture time"
                     />
                   </div>

                   <div className="mb-3">
                     <label htmlFor="topic_taught" className="form-label">Topic Taught <span className="text-danger">*</span></label>
                     <textarea
                       id="topic_taught"
                       name="topic_taught"
                       className="form-control"
                       value={formData.topic_taught}
                       onChange={handleChange}
                       required
                       rows="4"
                       maxLength="500"
                       aria-label="Topic taught"
                     ></textarea>
                   </div>

                   <div className="mb-3">
                     <label htmlFor="learning_outcomes" className="form-label">Learning Outcomes <span className="text-danger">*</span></label>
                     <textarea
                       id="learning_outcomes"
                       name="learning_outcomes"
                       className="form-control"
                       value={formData.learning_outcomes}
                       onChange={handleChange}
                       required
                       rows="4"
                       maxLength="500"
                       aria-label="Learning outcomes"
                     ></textarea>
                   </div>

                   <div className="mb-3">
                     <label htmlFor="recommendations" className="form-label">Recommendations</label>
                     <textarea
                       id="recommendations"
                       name="recommendations"
                       className="form-control"
                       value={formData.recommendations}
                       onChange={handleChange}
                       rows="4"
                       maxLength="500"
                       aria-label="Recommendations"
                     ></textarea>
                   </div>

                   <div className="mb-3">
                     <label htmlFor="prl_feedback" className="form-label">PRL Feedback</label>
                     <textarea
                       id="prl_feedback"
                       name="prl_feedback"
                       className="form-control"
                       value={formData.prl_feedback}
                       onChange={handleChange}
                       rows="4"
                       maxLength="500"
                       aria-label="PRL feedback"
                     ></textarea>
                   </div>

                   <button type="submit" className="btn btn-primary">Submit Report</button>
                 </form>
               </div>
             </div>
           </div>

           <div className="col-md-6">
             <div className="card mb-4">
               <div className="card-header">Create New Venue</div>
               <div className="card-body">
                 <form onSubmit={handleVenueSubmit}>
                   <div className="mb-3">
                     <label htmlFor="new_venue" className="form-label">Venue Name <span className="text-danger">*</span></label>
                     <input
                       type="text"
                       id="new_venue"
                       className="form-control"
                       value={newVenue}
                       onChange={(e) => setNewVenue(e.target.value)}
                       required
                       maxLength="100"
                       aria-label="New venue name"
                     />
                   </div>
                   <button type="submit" className="btn btn-primary">Create Venue</button>
                 </form>
               </div>
             </div>

             <div className="card">
               <div className="card-header">Create New Class</div>
               <div className="card-body">
                 <form onSubmit={handleClassSubmit}>
                   <div className="mb-3">
                     <label htmlFor="new_class_name" className="form-label">Class Name <span className="text-danger">*</span></label>
                     <input
                       type="text"
                       id="new_class_name"
                       name="name"
                       className="form-control"
                       value={newClass.name}
                       onChange={handleClassChange}
                       required
                       maxLength="50"
                       aria-label="New class name"
                     />
                   </div>
                   <button type="submit" className="btn btn-primary">Create Class</button>
                 </form>
               </div>
             </div>
           </div>
         </div>
       </div>
     );
   }

   export default PRLReportForm;
